            Set {changeModeTo} mode
          </button>
        </form>
      </div>
    </div>
  );
}

// /app/cookie.ts

import { createCookie } from "remix";

export const theme = createCookie("theme");

// /app/utils/parseCookie.ts

import { Cookie } from "remix";

export const parseCookie = async (request: Request, cookie: Cookie) => {
  const cookieHeader = request.headers.get("Cookie");
  const parsedCookie = (await cookie.parse(cookieHeader)) || {};
  return parsedCookie;
};

// /app/root.tsx

export const loader: LoaderFunction = async ({ request }) => {
  const cookie = await parseCookie(request, theme);
  if (!cookie.mode) cookie.mode = "light";

  return { mode: cookie.mode };
};

export const action: ActionFunction = async ({ request }) => {
  const cookie = await parseCookie(request, theme);
  const formData = await request.formData();

  cookie.mode = formData.get("mode") || cookie.mode || "light";

  return redirect("/", {
    headers: {
      "Set-Cookie": await theme.serialize(cookie),
    },
  });
};

